import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import axios from "axios";

// import fetch from 'isomorphic-unfetch';

const Index = () => {
  const [news, setNews] = useState([]);

 useEffect(()=>{

   axios.get(`https://api.tvmaze.com/search/shows?q=batman`)
  .then(res=>{console.log(res.data);setNews(res.data)})

},[])

  return (
    <Layout>
      <h1>TV Shows</h1>
      {news.map((value,index)=>{
        return(

          <di>
          <li key={index}><img style={{width:"100px" ,listStyle:"none"}} src={`${value.show.image.medium}`} alt=""/></li>
          <li>{value.show.name}</li>
          </di>
        )
      })}
    </Layout>
  );
};

export default Index;
