import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import axios from "axios";

// import fetch from 'isomorphic-unfetch';

const Index = () => {
  const [news, setNews] = useState([]);

 useEffect(()=>{

   axios.get(`https://api.tvmaze.com/search/shows?q=batman`)
  .then(res=>{console.log(res.data);setNews(res.data)})

},[])

  return (
    <Layout>
      <h1>TV Shows</h1>
      
      {news.map((value,index)=>{
        return(

          <div >
          <p key={index}><img style={{width:"200px" }} src={`${value.show.image.medium}`} alt=""/></p>
          <p style={{fontSize:"20px", color:"pink"}}>{value.show.name}</p>
          
          <style>{`
          
          .rr{
            display: grid;
            grid-template-columns: 1fr 1fr 1fr;

          }
          
          `}</style>
          
          </div>
        )
      })}
    </Layout>
  );
};

export default Index;
