import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import axios from "axios";
import Link from "next/link";




const Index = props => {
  // const [news, setNews] = useState([]);

  //  useEffect(()=>{

  //    axios.get(`https://api.tvmaze.com/search/shows?q=batman`)
  //   .then(res=>{console.log(res.data);setNews(res.data)})

  // },[])

  const [searchQuery, setSearchQuery] = useState('batman');

  const handleChange =e=>{
    setSearchQuery(e.target.value)
  }

  return (
    <Layout>
      <h1>TV Shows</h1>
      <div>
        <input type="text" value={searchQuery} onChange={handleChange}/>
        {console.log(props)}
        {props.res.map((value, index) => {
          return (
            <div key={index}>
              <Link href="/p/[id]/" as={`/p/${value.show.id}`}>
                <a>{value.show.name}</a>
              </Link>
            </div>
          );
        })}
      </div>
    </Layout>
  );
};

Index.getInitialProps = async (props) => {

  const res = await axios.get(`https://api.tvmaze.com/search/shows?q=${props.searchQuery}`);
  return { res: res.data };
};

export default Index;
